export declare const theme: {
    breakpoints: string[];
    space: (string | number)[];
    fonts: {
        body: string;
        heading: string;
        monospace: string;
    };
    fontSizes: string[];
    fontWeights: {
        body: number;
        heading: number;
        bold: number;
    };
    lineHeights: {
        body: number;
        heading: number;
    };
    colors: {
        text: string;
        background: string;
        primary: string;
        secondary: string;
        muted: string;
        border: string;
        warning: string;
        error: string;
    };
    text: {
        heading: {
            fontFamily: string;
            lineHeight: string;
            fontWeight: string;
        };
    };
    styles: {
        root: {
            fontFamily: string;
            lineHeight: string;
            fontWeight: string;
        };
        h1: {
            variant: string;
            fontSize: number;
        };
        h2: {
            variant: string;
            fontSize: number;
        };
        h3: {
            variant: string;
            fontSize: number;
        };
        h4: {
            variant: string;
            fontSize: number;
        };
        h5: {
            variant: string;
            fontSize: number;
        };
        h6: {
            variant: string;
            fontSize: number;
        };
        pre: {
            fontFamily: string;
            overflowX: string;
            code: {
                color: string;
            };
        };
        code: {
            fontFamily: string;
            fontSize: string;
        };
        table: {
            width: string;
            borderCollapse: string;
            borderSpacing: number;
        };
        th: {
            textAlign: string;
            borderBottomStyle: string;
        };
        td: {
            textAlign: string;
            borderBottomStyle: string;
        };
    };
};
